function loadContent(n,t,i,r){$("#content-title").empty().append($(n).attr("title"));$("ul.sidebar-menu li.active").attr("class","");$(n).parent().attr("class","active");$.get("/"+t+"/"+i+"?"+r,function(n){var t=$("#content");t.empty().append(n)})}function stopTask(n){$.get("/taskstatus/stop/?identity="+n,function(n){n==="OK"?layer.alert("\u505c\u6b62\u6210\u529f",{icon:1}):layer.alert("\u505c\u6b62\u5931\u8d25",{icon:2})})}